import React from 'react';
import confetti from "https://esm.run/canvas-confetti@1";

export default function About() {

  var scalar = 2;
  var pineapple = confetti.shapeFromText({ text: 'ðŸ˜‚', scalar});
  var locate = document.getElementById("howdy").target.scrollTop; 

  function boom() {
    confetti({
      gravity: 0,
      flat:true,
      shapes: [pineapple],
      scalar,
      origin: {
        x: Math.random(),
        // since they fall down, start a bit higher than random
        y: locate
      }
    });
  }

  function hehe () {
    <img src={require('./assets/background_image.png')} className='m w-[100%] object-cover rounded-full z-10'/>
  }


  return (
    <section id="about" className='h-[90vh] lg:ml-[20%] lg:mr-[0px] mx-[5%] mt-[7%]'>
      {/* ONE CONTAINER with a FLEXBOX cloumn that has 3 items in it (heading, p, and inline flexboxes*/}
      {/* ***NOTE*** rounded-'large' is a custom theme on tailwind. look at tailwind.config file shape specifics */}
      {/* size l is when the sidebar is revieled and navbar at top is hidden */}
      <div className='rounded-heffer bg-gradient-to-r from-cyan-300/90 via-emerald-300/90 to-sky-300/[.95] flex flex-col p-8 mb-8  sm:my-16 lg:mb-16 lg:mr-16 mt-8 4xl:mr-24 5xl:mr-32 6xl:mr-40 items-center text-center' >


        <img  src={require('./assets/pfp.png')} className = 'm w-[15%] object-cover rounded-full' alt='black and white of Antonio in a gray light jacket with a blue and black patterned button down shirt underneath. He is also wearing a blue jean cap.'/> 

        <h1 id="howdy" className="title-font text-[250%] my-6 md:mb-8 mx-auto text-white hover:text-indigo-500 drop-shadow cursor-pointer" onMouseOver={hehe} onClick={boom}>
          Howdy, I'm Antonio.
        </h1>
        <p className="text-white mb-8 mx-8 lg:mr-20 leading-relaxed break-normal drop-shadow-sm">
          Information Technology student, programmer, and wannabe fullstack developer. Wrangling code since 2019
        </p>
        {/* two "buttons" that are rlly just <a> in a flexbox with hover color changing */}
        <div className="flex justify-content-around m-2">
          <a
            onClick={() => document.getElementById("contact").scrollIntoView()}
            className="inline-flex text-slate-100 border border-2 border-indigo-500 bg-indigo-500/90 mb-2 py-2 px-6 hover:text-slate-300 hover:border-indigo-600 hover:bg-indigo-600 rounded-2xl text-lg">
            Contact Me
          </a>
          <a
            onClick={() => document.getElementById("projects").scrollIntoView()}
            className="ml-4 inline-flex text-slate-100 border border-2 border-blue-500 bg-blue-500/90 mb-2 py-2 px-6 hover:bg-blue-600 hover:border-blue-600 hover:text-slate-300 rounded-2xl text-lg">
            Past Projects
          </a>
        </div>
      </div>
    </section>
  );
}
