import React from "react";
import { Outlet,
  Routes,
  Route, } from 'react-router-dom';
import './index.css';
import Navbar from "./Components/Navbar/index";
import WorkExperience from "./Components/WorkExperience";
import Projects from "./Components/Projects";
import Skills from "./Components/Skills";
import Contact from "./Components/Contact";
import About from "./Components/About";
import Education from "./Components/Education";
import Foot from "./Components/Foot";
import useScrollPosition from "./useScrollPosition";


const changeBackground = (e) => {
  // PRINTS Y POSITION OF SCROLLING console.log(e.target.scrollTop)
  if (e.target.scrollTop > 15 ) { 
    document.getElementById('mainApp').setAttribute('style', 'visibility: hidden; opacity: 0; transition: visibility 400ms, opacity 0.5s linear;');
  }
  else {
    document.getElementById('mainApp').setAttribute('style', 'visibility: visible; opacity: 100; transition: visibility 1s, opacity 0.5s linear;');
  } 
};

export default function App () { 
  // const scrollPosition = useScrollPosition();

  // console.log(scrollPosition);

  return (
    <div id='mainApp' onScroll={changeBackground}>
      <Navbar />
      <About/>
      <Education/>
      <WorkExperience/>
      <Skills/>
      <Projects/>
      <Contact/>

      <Routes>
      <Route path="/" >
        <Route path="about" />
        <Route path="contact" />
        <Route path="education"/>
        <Route path="workexperience" />
        <Route path="projects&activities" />
        <Route path="skills"/>
        <Route
          path="*"
          element={
            <main style={{ padding: "1rem" }}>
              <p>There's nothing here!</p>
            </main>
          }
        />
      </Route>
    </Routes>
    <Foot/>
      <Outlet />
    </div>
  );
}
