import React from "react";
import { TopNav, NavLink }
 from "./Navbar";
import waterlogo from "../assets/water-logo.mp4";
import { useRef } from "react";

function scrollTo(ref) {
    if (!ref.current) return;
    ref.current.scrollIntoView({ behavior: "smooth" });
  }

/*  function aboutScroll() {
    const element = document.getElementById("about");
    return document.getElementById(element).scrollIntoView(true);
  }   */


const Navbar = () => {

const aboutRef = useRef();

return (
 <TopNav>

 <video src= {waterlogo} autoPlay loop muted className="lg:p-[.5rem] lg:mb-2 lg:w-[30vh] lg:a w-[0px]"/>

  <NavLink to="/about " onClick={aboutRef.current.scrollIntoView()}  activeStyle>
   About
  </NavLink>
  <NavLink to="/workexperience" activeStyle>
   Work Experience
  </NavLink>
  <NavLink to="/education" activeStyle>
   Education
  </NavLink>
  <NavLink to="/skills" activeStyle>
   Skills
  </NavLink>
  <NavLink to="/projects&activities" activeStyle>
   Projects and Activities
  </NavLink>
  <NavLink className ="lg:mb-36" to="/contact" activeStyle>
   Contact
  </NavLink>

 </TopNav>
);
}

export default Navbar ;
