import React from "react";
import { Outlet,
  Routes,
  Route, } from 'react-router-dom';
import './index.css';
import Navbar from "./Components/Navbar/index";
import WorkExperience from "./Components/WorkExperience";
import Projects from "./Components/Projects";
import Skills from "./Components/Skills";
import Contact from "./Components/Contact";
import About from "./Components/About";
import Education from "./Components/Education";
import Foot from "./Components/Foot";

const runOnScroll = (e) => {
  // PRINTS Y POSITION OF SCROLLING console.log(e.target.scrollTop)
  if (e.target.scrollTop > 15 ) { 
    document.getElementById('navi').setAttribute('style', 'visibility: hidden; opacity: 0; transition: visibility 400ms, opacity 0.5s linear;');
  }
  else {
    document.getElementById('navi').setAttribute('style', 'visibility: visible; opacity: 100; transition: visibility 1s, opacity 0.5s linear;');
  } 
};

var didScroll;
var lastScrollTop = 0;
var delta = 5;
var navbarHeight = $('navi').outerHeight();

$(window).scroll(function(event){
    didScroll = true;
});

setInterval(function() {
    if (didScroll) {
        hasScrolled();
        didScroll = false;
    }
}, 250);

function hasScrolled() {
    var st = $(this).scrollTop();

    // Make sure they scroll more than delta
    if(Math.abs(lastScrollTop - st) <= delta)
        return;

    // If they scrolled down and are past the navbar, add class .nav-up.
    // This is necessary so you never see what is "behind" the navbar.
    if (st > lastScrollTop && st > navbarHeight){
        // Scroll Down
        $('navi').removeClass('nav-down').addClass('nav-up');
    } else {
        // Scroll Up
        if(st + $(window).height() < $(document).height()) {
            $('header').removeClass('nav-up').addClass('nav-down');
        }
    }

    lastScrollTop = st;
}

export default function App () { 

  return (
    <div>
      <Navbar id='navi' OnScroll={runOnScroll}/>
      <About/>
      <Education/>
      <WorkExperience/>
      <Skills/>
      <Projects/>
      <Contact/>

      <Routes>
      <Route path="/" >
        <Route path="about" />
        <Route path="contact" />
        <Route path="education"/>
        <Route path="workexperience" />
        <Route path="projects&activities" />
        <Route path="skills"/>
        <Route
          path="*"
          element={
            <main style={{ padding: "1rem" }}>
              <p>There's nothing here!</p>
            </main>
          }
        />
      </Route>
    </Routes>
    <Foot/>
      <Outlet />
    </div>
  );
}
