import React from "react";
import { TopNav, NavLink }
 from "./Navbar";
import waterlogo from "../assets/water-logo.mp4";
import useScrollPosition from "../../useScrollPosition";

const ScrollMenuHandle  = () => {
   const scrollPosition = useScrollPosition();

   return console.log(scrollPosition);
}

export default function Navbar() {

// ****** GETS SCROLL POSITION USING HOOK*******



    //UNCHECKS CHECKBOX TO HIDE MENU
    function closeSide(){
         document.getElementById('my-drawer-2').checked=false;
    }
    function aboutScroll() {
        document.getElementById('about').scrollIntoView({block: "end" });
        closeSide()
    }
    function educationScroll() {
        document.getElementById('education').scrollIntoView({block: "end" });
        closeSide()
    }
    function workScroll() {
        document.getElementById('work').scrollIntoView({block: "end" });
        closeSide()
    }
    function skillsScroll() {
        document.getElementById('skills').scrollIntoView({block: "end" });
        closeSide()
    }
    function projectsScroll() {
        document.getElementById('projects').scrollIntoView({block: "end" });
        closeSide()
    }
    function contactScroll() {
        document.getElementById('contact').scrollIntoView({block: "end" });
        closeSide()
    }


return (
    <ScrollMenuHandle> 
    <TopNav className='drawer lg:drawer-open lg:w-[100%]' > 

    <input id="my-drawer-2" type="checkbox" className="drawer-toggle"/> 

    <div className="drawer-content lg:hidden">
        {/* Page content here */}
        <label htmlFor="my-drawer-2" className="btn btn-primary drawer-button lg:hidden">Open drawer</label>
    </div>

    <div className="drawer-side">
        <label htmlFor="my-drawer-2" aria-label="close sidebar" className="drawer-overlay"></label> 
        <ul className="menu w-80 min-h-full bg-[#ffb3fffc] lg:w-[100%]">
        {/* Sidebar content here */}
        <video src= {waterlogo} autoPlay loop muted className="invisible lg:visible lg:p-[.5rem] lg:mb-2 lg:w-[100%]"/>

        <NavLink to="/about " onClick={aboutScroll}>
            About
        </NavLink>
        <NavLink to="/education" onClick={educationScroll} activeStyle>
            Education
        </NavLink>
        <NavLink to="/workexperience" onClick={workScroll} activeStyle>
            Work Experience
        </NavLink>
        <NavLink to="/skills" onClick={skillsScroll} activeStyle>
            Skills
        </NavLink>
        <NavLink to="/projects&activities" onClick={projectsScroll} activeStyle>
            Projects and Activities
        </NavLink>
        <NavLink className ="lg:mb-36" to="/contact" onClick={contactScroll} activeStyle>
            Contact
        </NavLink>

        </ul>
    </div>

    </TopNav> </ScrollMenuHandle> 



// ####### OLD NAVBAR ##########
