        <label htmlFor="my-drawer-2" aria-label="close sidebar" className="drawer-overlay"></label> 
        <ul className="menu w-80 min-h-full bg-[#ffb3fffc] lg:w-[100%]">
        {/* Sidebar content here */}
        <video src= {waterlogo} autoPlay loop muted className="invisible lg:visible lg:p-[.5rem] lg:mb-2 lg:w-[100%]"/>

        <NavLink to="/about " onClick={aboutScroll}>
            About
        </NavLink>
        <NavLink to="/education" onClick={educationScroll}>
            Education
        </NavLink>
        <NavLink to="/workexperience" onClick={workScroll}>
            Work Experience
        </NavLink>
        <NavLink to="/skills" onClick={skillsScroll} >
            Skills
        </NavLink>
        <NavLink to="/projects&activities" onClick={projectsScroll} >
            Projects and Activities
        </NavLink>
        <NavLink className ="lg:mb-36" to="/contact" onClick={contactScroll} >
            Contact
        </NavLink>

        </ul>
    </div>

